import React from 'react';
import './Machine.css';
import RotateArrow from './rotate-arrow.png';

export default class Machine extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      available: true,
      broken: false
    };
    this.toggleMachineStatus = this.toggleMachineStatus.bind(this);
  }

  toggleMachineStatus() {
    this.setState({
      available: !this.state.available,
    });
  }

  render() {
    const isAvailable = this.state.available;
    let machineStyle, imgStyle, buttonStatus;
    if(isAvailable) {
      machineStyle = {
        background: 'linear-gradient(to bottom, #38ef7d, #11998e)', /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
        color: 'white'
      };
      imgStyle = 'rotateImg';
      buttonStatus = 'Use';
    } else {
      machineStyle = {
        background: 'linear-gradient(from center, #ff6a00, #ee0979)', /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
        color: 'white'
      };
      imgStyle = 'rotateImg rotateImgActive';
      buttonStatus = 'Free up';
    }
    return (
      <div>
        <div className="machine" style={machineStyle}>
          <h4 className="machineType">{this.props.machineType}</h4>
          <img className={imgStyle} src={RotateArrow} />
          <button className="machineButton" onClick={this.toggleMachineStatus}>{buttonStatus}</button>
        </div>
      </div>
    );
  }
}
