import React from 'react';
import './LaundryRoom.css';

function Machine(props) {
  return(
    <div>
      <button className={props.machineType}>{props.machineType}</button>
    </div>
  )
}
export default class LaundryRoom extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      available: false,
      broken: false
    };
    this.toggleMachineStatus = this.toggleMachineStatus.bind(this);
  }

  toggleMachineStatus() {
    this.setState({
      available: !this.state.available,
    });
  }

  render() {
    const isAvailable = this.state.available;
    let buttonStyle;
    if(isAvailable) {
      buttonStyle = {color: 'red'};
    } else {
      buttonStyle = {color:'green'};
    }
    return (
      <div>
        <button onClick={this.toggleMachineStatus} style={buttonStyle}>Washer</button>
      </div>
    );
  }
}
