import React from 'react';
import './LaundryRoom.css';

export default class LaundryRoom extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inUse: false,
      broken: false
    };
    this.toggleMachineStatus = this.toggleMachineStatus.bind(this);
  }

  toggleMachineStatus() {
    this.setState({
      inUse: !inUse,
    });
  }

  render() {
    const isInUse = this.state.inUse;
    let buttonStyle;
    if(inUse) {
      buttonStyle = 'color: red';
    } else {
      buttonStyle = 'color: green';
    }
    return (
      <div>
        <button style={buttonStyle}>Washer</button>
      </div>
    );
  }
}
